import React, { useState } from "react";

function GetHour() {

    const [clockState, setClockState] = useState();

    useState(() => {
        setInterval(() => {
            const date = new Date();
            setClockState(date.getHours());
        }, 1000);
    })

    return (
        <div id="clock">{clockState}</div>
    )
}





export default function FullTime() {

    return (
        <div>
            <div id="clock">{GetHour()}</div>
            <div id="clock">{GetMinute()}</div>
            <div id="clock">{GetSecond()}</div>
        </div>
    )
}
