import React, {useState } from 'react'
/*
-Clock fonksiyonu olusturuldu
-Date nesnesi olusturuldu ve local time alindi
-State ile Data setInterval fonksiyonu icerisinde hersaniyede kullanildi



*/
export default function WorldDigitalClock() {

    const [clockState, setClockState] = useState();

    useState(() => {  
        setInterval(() => {
            const date = new Date();
            setClockState(date.toLocaleTimeString());
        }, 1000);
    })   

  return (
    <div id="clock">{clockState}</div>
  )
}

 function GetHour() {

    const [clockState, setClockState] = useState();

    useState(() => {  
        setInterval(() => {
            const date = new Date();
            setClockState(date.GetHour());
        }, 1000);
    })   

  return (
    <div id="clock">{clockState}</div>
  )
}

function GetMinute() {

    const [clockState, setClockState] = useState();

    useState(() => {  
        setInterval(() => {
            const date = new Date();
            setClockState(date.getMinutes());
        }, 1000);
    })   

  return (
    <div id="clock">{clockState}</div>
  )
}
 
function GetSecond() {

    const [clockState, setClockState] = useState();

    useState(() => {  
        setInterval(() => {
            const date = new Date();
            setClockState(date.getSeconds());
        }, 1000);
    })   

  return (
    <div id="clock">{clockState}</div>
  )
}
